import React from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ usuarioActual, setEstaLogueado, setUsuarioActual }) => {
  const navigate = useNavigate();

  const cerrarSesion = () => {
    sessionStorage.clear();
    setEstaLogueado(false);
    setUsuarioActual(null);
    navigate('/');
  };

  return (
    <div style={styles.background}>
      <div style={styles.container}>
        <h2 style={styles.title}>Bienvenido, {usuarioActual}</h2>
        <p style={styles.subtitle}>Selecciona una sección para continuar</p>

        <button style={styles.button} onClick={() => navigate('/autores')}>
          Gestionar Autores
        </button>
        <button style={styles.button} onClick={() => navigate('/libros')}>
          Gestionar Libros
        </button>

        <button style={styles.logout} onClick={cerrarSesion}>
          Cerrar Sesión
        </button>
      </div>
    </div>
  );
};

const styles = {
  background: {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #e0c3fc 0%, #8ec5fc 100%)',
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
  },
  container: {
    width: '100%',
    maxWidth: '400px',
    padding: '30px',
    borderRadius: '16px',
    backgroundColor: '#fff',
    boxShadow: '0 8px 20px rgba(0,0,0,0.15)',
    textAlign: 'center',
  },
  title: {
    fontSize: '26px',
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: '10px',
  },
  subtitle: {
    fontSize: '14px',
    color: '#7f8c8d',
    marginBottom: '20px',
  },
  button: {
    width: '100%',
    padding: '12px',
    margin: '10px 0',
    backgroundColor: '#3498db',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  logout: {
    marginTop: '20px',
    backgroundColor: '#e74c3c',
    color: '#fff',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '8px',
    fontSize: '14px',
    cursor: 'pointer',
  },
};

export default Dashboard;
