import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import AutorList from './AutorList';

const App = () => {
  const estaLogueado = localStorage.getItem('logueado') === 'true';

  return (
    <Router>
      <Routes>
        <Route path="/" element={estaLogueado ? <Navigate to="/autores" /> : <Login />} />
        <Route path="/autores" element={estaLogueado ? <AutorList /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
