// ForgottenPassword.jsx
import React, { useState } from 'react';

const ForgottenPassword = () => {
  const [usuario, setUsuario] = useState('');
  const [pregunta, setPregunta] = useState('');
  const [respuestaUsuario, setRespuestaUsuario] = useState('');
  const [respuestaCorrecta, setRespuestaCorrecta] = useState('');
  const [usuarioId, setUsuarioId] = useState('');
  const [nuevaPassword, setNuevaPassword] = useState('');
  const [fase, setFase] = useState(1); // 1: username, 2: respuesta, 3: nueva contraseña

  const BASE_URL = 'https://mongodbbacken.onrender.com';

  const verificarUsuario = async () => {
    if (!usuario.trim()) {
      alert('Debes ingresar un nombre de usuario.');
      return;
    }

    try {
      const res = await fetch(`${BASE_URL}/api/usuarios`);
      const usuarios = await res.json();
      const encontrado = usuarios.find(u => u.usuario === usuario.toLowerCase());

      if (encontrado) {
        setPregunta(encontrado.pregunta);
        setRespuestaCorrecta(encontrado.respuesta);
        setUsuarioId(encontrado._id);
        setFase(2);
      } else {
        alert('Usuario no encontrado.');
      }
    } catch (error) {
      console.error(error);
      alert('Error al verificar el usuario.');
    }
  };

  const verificarRespuesta = () => {
    if (respuestaUsuario.trim().toLowerCase() === respuestaCorrecta.toLowerCase()) {
      setFase(3);
    } else {
      alert('Respuesta incorrecta.');
    }
  };

  const cambiarPassword = async () => {
    if (!nuevaPassword.trim()) {
      alert('Debes ingresar una nueva contraseña.');
      return;
    }

    try {
      const res = await fetch(`${BASE_URL}/api/usuarios/${usuarioId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ password: nuevaPassword }),
      });

      if (res.ok) {
        alert('Contraseña actualizada correctamente.');
        window.location.href = '/';
      } else {
        alert('Error al actualizar contraseña.');
      }
    } catch (error) {
      console.error(error);
      alert('Error en el servidor al cambiar la contraseña.');
    }
  };

  return (
    <div style={styles.background}>
      <div style={styles.container}>
        <h2 style={styles.title}>Recuperar Contraseña</h2>
        {fase === 1 && (
          <>
            <p style={styles.subtitle}>Ingresa tu nombre de usuario</p>
            <input
              style={styles.input}
              type="text"
              placeholder="Nombre de usuario"
              value={usuario}
              onChange={(e) => setUsuario(e.target.value)}
            />
            <button style={styles.button} onClick={verificarUsuario}>
              Verificar Usuario
            </button>
          </>
        )}

        {fase === 2 && (
          <>
            <p style={styles.subtitle}><strong>Pregunta secreta:</strong> {pregunta}</p>
            <input
              style={styles.input}
              type="text"
              placeholder="Respuesta"
              value={respuestaUsuario}
              onChange={(e) => setRespuestaUsuario(e.target.value)}
            />
            <button style={styles.button} onClick={verificarRespuesta}>
              Validar Respuesta
            </button>
          </>
        )}

        {fase === 3 && (
          <>
            <p style={styles.subtitle}>Ingresa tu nueva contraseña</p>
            <input
              style={styles.input}
              type="password"
              placeholder="Nueva contraseña"
              value={nuevaPassword}
              onChange={(e) => setNuevaPassword(e.target.value)}
            />
            <button style={styles.button} onClick={cambiarPassword}>
              Cambiar Contraseña
            </button>
          </>
        )}
      </div>
    </div>
  );
};

const styles = {
  background: {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)',
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
  },
  container: {
    width: '100%',
    maxWidth: '420px',
    padding: '30px',
    borderRadius: '12px',
    backgroundColor: '#ffffff',
    boxShadow: '0 10px 25px rgba(0,0,0,0.15)',
    textAlign: 'center',
  },
  title: {
    fontSize: '26px',
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: '10px',
  },
  subtitle: {
    fontSize: '14px',
    color: '#7f8c8d',
    marginBottom: '15px',
  },
  input: {
    width: '100%',
    padding: '12px',
    marginBottom: '10px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    fontSize: '16px',
    outline: 'none',
    transition: 'border-color 0.3s ease',
  },
  button: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#e67e22',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    marginTop: '10px',
  },
};

export default ForgottenPassword;
