// Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = ({ setEstaLogueado, setUsuarioActual }) => {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const manejarLogin = async () => {
    if (!usuario || !password) {
      alert('Por favor ingresa usuario y contraseña');
      return;
    }

    try {
      const res = await fetch('https://mongodbbacken.onrender.com/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ usuario: usuario.toLowerCase(), password }),
      });

      const data = await res.json();

      if (res.ok) {
        alert(data.mensaje);
        sessionStorage.setItem('logueado', 'true');
        sessionStorage.setItem('usuario', usuario.toLowerCase());
        setEstaLogueado(true);
        setUsuarioActual(usuario.toLowerCase());
        navigate('/autores');
      } else {
        alert(data.mensaje || 'Credenciales incorrectas');
      }
    } catch (err) {
      console.error(err);
      alert('Error al iniciar sesión');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Iniciar Sesión</h2>
      <input
        style={styles.input}
        type="text"
        placeholder="Usuario"
        value={usuario}
        onChange={(e) => setUsuario(e.target.value)}
      />
      <input
        style={styles.input}
        type="password"
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button style={styles.button} onClick={manejarLogin}>
        Entrar
      </button>
      <p style={styles.link} onClick={() => navigate('/forgot-password')}>
        ¿Olvidaste tu contraseña?
      </p>
      <p style={styles.link} onClick={() => navigate('/register')}>
        ¿No tienes cuenta? Regístrate aquí
      </p>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '400px',
    margin: '60px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  title: {
    textAlign: 'center',
    marginBottom: '20px',
    color: '#333',
  },
  input: {
    display: 'block',
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    fontSize: '16px',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#3498db',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  link: {
    textAlign: 'center',
    color: '#2980b9',
    cursor: 'pointer',
    marginTop: '10px',
  },
};

export default Login;
