import React, { useEffect, useState, useCallback, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const API_URL = 'https://www.librostoken.somee.com/api/LibroMaterial';
const TOKEN = 'TU_TOKEN_AQUI'; // ‚Üê Reemplaza con tu token real

export default function LibroMaterialView() {
  const navigate = useNavigate();

  const [libros, setLibros] = useState([]);
  const [form, setForm] = useState({ titulo: '', fechaPublicacion: '' });
  const [editandoId, setEditandoId] = useState(null);

  // Memoiza headers para que no cambien cada render
  const headers = useMemo(() => ({
    Authorization: `Bearer ${TOKEN}`,
    'Content-Type': 'application/json',
  }), [TOKEN]);

  const cargarLibros = useCallback(async () => {
    try {
      const res = await axios.get(API_URL, { headers });
      setLibros(res.data);
    } catch (err) {
      console.error('Error al cargar libros', err);
      alert('Error al cargar libros');
    }
  }, [headers]);

  useEffect(() => {
    cargarLibros();
  }, [cargarLibros]);

  const manejarCambio = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const manejarEnvio = async (e) => {
    e.preventDefault();
    if (!form.titulo.trim() || !form.fechaPublicacion) {
      alert('Debe completar todos los campos');
      return;
    }

    const payload = {
      titulo: form.titulo.trim(),
      fechaPublicacion: form.fechaPublicacion,
    };

    try {
      if (editandoId) {
        await axios.put(`${API_URL}/${editandoId}`, payload, { headers });
        setEditandoId(null);
      } else {
        await axios.post(API_URL, payload, { headers });
      }

      setForm({ titulo: '', fechaPublicacion: '' });
      cargarLibros();
    } catch (err) {
      console.error('Error al guardar libro', err);
      alert('Error al guardar libro');
    }
  };

  const manejarEditar = (libro) => {
    setForm({
      titulo: libro.titulo,
      fechaPublicacion: libro.fechaPublicacion?.slice(0, 10) || '',
    });
    setEditandoId(libro.libreriaMaterialId);
  };

  const manejarEliminar = async (id) => {
    if (!window.confirm('¬øEst√°s seguro de eliminar este libro?')) return;
    try {
      await axios.delete(`${API_URL}/${id}`, { headers });
      cargarLibros();
    } catch (err) {
      console.error('Error al eliminar libro', err);
      alert('Error al eliminar libro');
    }
  };

  const regresarDashboard = () => {
    navigate('/dashboard');
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-semibold mb-4 text-center">üìö Gesti√≥n de Libros</h2>

      <button
        onClick={regresarDashboard}
        className="mb-4 bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 transition"
      >
        ‚Üê Regresar al Dashboard
      </button>

      <form
        onSubmit={manejarEnvio}
        className="flex flex-col gap-3 bg-gray-100 p-4 rounded-lg shadow-md"
      >
        <input
          type="text"
          name="titulo"
          placeholder="T√≠tulo"
          value={form.titulo}
          onChange={manejarCambio}
          required
          className="p-2 border rounded"
        />
        <input
          type="date"
          name="fechaPublicacion"
          value={form.fechaPublicacion}
          onChange={manejarCambio}
          required
          className="p-2 border rounded"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white p-2 rounded hover:bg-blue-700 transition"
        >
          {editandoId ? 'Actualizar Libro' : 'Crear Libro'}
        </button>
      </form>

      <table className="mt-6 w-full table-auto border-collapse">
        <thead>
          <tr className="bg-blue-100">
            <th className="border p-2">T√≠tulo</th>
            <th className="border p-2">Fecha Publicaci√≥n</th>
            <th className="border p-2">AutorLibro</th>
            <th className="border p-2">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {libros.map((libro) => (
            <tr key={libro.libreriaMaterialId} className="hover:bg-gray-100">
              <td className="border p-2">{libro.titulo}</td>
              <td className="border p-2">{libro.fechaPublicacion?.split('T')[0]}</td>
              <td className="border p-2 text-gray-500">{libro.autorLibro}</td>
              <td className="border p-2 flex gap-2 justify-center">
                <button
                  onClick={() => manejarEditar(libro)}
                  className="bg-yellow-400 px-3 py-1 rounded hover:bg-yellow-500"
                >
                  Editar
                </button>
                <button
                  onClick={() => manejarEliminar(libro.libreriaMaterialId)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
