// AutorList.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // para navegaci√≥n program√°tica

const BASE_URL = 'https://www.autoreslibrossss.somee.com/api/Autor';

const AutorList = ({ usuarioActual, setEstaLogueado, setUsuarioActual }) => {
  const navigate = useNavigate();

  const [autores, setAutores] = useState([]);
  const [error, setError] = useState('');
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [fechaNacimiento, setFechaNacimiento] = useState('');
  const [guidSeleccionado, setGuidSeleccionado] = useState(null);

  useEffect(() => {
    obtenerAutores();
  }, []);

  const obtenerAutores = async () => {
    try {
      const token = sessionStorage.getItem('accessToken');
      const response = await axios.get(BASE_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAutores(response.data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('No se pudo cargar la lista de autores.');
    }
  };

  const validarFormulario = () => {
    if (!nombre.trim() || !apellido.trim() || !fechaNacimiento.trim()) {
      alert('Todos los campos son obligatorios.');
      return false;
    }

    const fecha = new Date(fechaNacimiento);
    const hoy = new Date();
    hoy.setHours(0, 0, 0, 0);

    if (fecha > hoy) {
      alert('La fecha de nacimiento no puede ser mayor a hoy.');
      return false;
    }

    return true;
  };

  const limpiarFormulario = () => {
    setNombre('');
    setApellido('');
    setFechaNacimiento('');
    setGuidSeleccionado(null);
  };

  const cerrarSesion = () => {
    sessionStorage.clear();
    setEstaLogueado(false);
    setUsuarioActual(null);
    navigate('/'); // usar navigate para mejor control
  };

  const crearAutor = async () => {
    if (!validarFormulario()) return;

    try {
      const token = sessionStorage.getItem('accessToken');
      const fechaISO = new Date(fechaNacimiento).toISOString();
      await axios.post(
        BASE_URL,
        {
          nombre,
          apellido,
          fechaNacimiento: fechaISO,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      obtenerAutores();
      limpiarFormulario();
    } catch (error) {
      console.error(error);
      alert('Error al crear autor.');
    }
  };

  const actualizarAutor = async () => {
    if (!validarFormulario()) return;

    try {
      const token = sessionStorage.getItem('accessToken');
      const fechaISO = new Date(fechaNacimiento).toISOString();
      await axios.put(
        `${BASE_URL}/${guidSeleccionado}`,
        {
          autorLibroGuid: guidSeleccionado,
          nombre,
          apellido,
          fechaNacimiento: fechaISO,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      obtenerAutores();
      limpiarFormulario();
    } catch (error) {
      console.error(error);
      alert('Error al actualizar autor.');
    }
  };

  const eliminarAutor = async (guid) => {
    if (!window.confirm('¬øSeguro que deseas eliminar este autor?')) return;

    try {
      const token = sessionStorage.getItem('accessToken');
      await axios.delete(`${BASE_URL}/${guid}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      obtenerAutores();
    } catch (error) {
      console.error(error);
      alert('Error al eliminar autor.');
    }
  };

  const seleccionarParaEditar = (autor) => {
    setGuidSeleccionado(autor.autorLibroGuid);
    setNombre(autor.nombre);
    setApellido(autor.apellido);
    setFechaNacimiento(autor.fechaNacimiento.split('T')[0]);
  };

  const formatearFecha = (fechaString) => {
    const fecha = new Date(fechaString);
    return fecha.toLocaleDateString('es-MX', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const regresarDashboard = () => {
    navigate('/dashboard');
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Gesti√≥n de Autores</h1>
        <div>
          <span style={styles.user}>üë§ {usuarioActual}</span>
          <button style={styles.logoutButton} onClick={cerrarSesion}>
            Cerrar sesi√≥n
          </button>
        </div>
      </div>

      <button style={styles.backButton} onClick={regresarDashboard}>
        ‚Üê Regresar al Dashboard
      </button>

      {error && <div style={styles.error}>{error}</div>}

      <div style={styles.form}>
        <input
          style={styles.input}
          placeholder="Nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
        />
        <input
          style={styles.input}
          placeholder="Apellido"
          value={apellido}
          onChange={(e) => setApellido(e.target.value)}
        />
        <input
          style={styles.input}
          type="date"
          value={fechaNacimiento}
          onChange={(e) => setFechaNacimiento(e.target.value)}
        />
        <button
          style={styles.buttonPrimary}
          onClick={guidSeleccionado ? actualizarAutor : crearAutor}
        >
          {guidSeleccionado ? 'Actualizar' : 'Crear'}
        </button>
        <button style={styles.buttonSecondary} onClick={limpiarFormulario}>
          Cancelar
        </button>
      </div>

      <div style={styles.tableWrapper}>
        <table style={styles.table}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Apellido</th>
              <th>Fecha Nacimiento</th>
              <th>GUID</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {autores.map((autor) => (
              <tr key={autor.autorLibroGuid}>
                <td>{autor.autorLibroId}</td>
                <td>{autor.nombre}</td>
                <td>{autor.apellido}</td>
                <td>{formatearFecha(autor.fechaNacimiento)}</td>
                <td style={{ fontSize: '0.8rem', wordBreak: 'break-all' }}>
                  {autor.autorLibroGuid}
                </td>
                <td>
                  <button
                    style={styles.editButton}
                    onClick={() => seleccionarParaEditar(autor)}
                  >
                    ‚úèÔ∏è
                  </button>
                  <button
                    style={styles.deleteButton}
                    onClick={() => eliminarAutor(autor.autorLibroGuid)}
                  >
                    üóëÔ∏è
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '40px',
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
    backgroundColor: '#ecf0f1',
    minHeight: '100vh',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '30px',
  },
  title: {
    fontSize: '2.5rem',
    color: '#2c3e50',
  },
  user: {
    marginRight: '20px',
    fontSize: '1rem',
    color: '#34495e',
  },
  logoutButton: {
    padding: '8px 16px',
    backgroundColor: '#e74c3c',
    color: '#fff',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    transition: 'background 0.3s',
  },
  backButton: {
    marginBottom: '20px',
    padding: '8px 16px',
    backgroundColor: '#34495e',
    color: '#fff',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    transition: 'background 0.3s',
  },
  error: {
    backgroundColor: '#ffcccc',
    color: '#c0392b',
    padding: '10px',
    borderRadius: '6px',
    marginBottom: '20px',
