import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AutorList = () => {
  const [autores, setAutores] = useState([]);
  const [error, setError] = useState('');
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [fechaNacimiento, setFechaNacimiento] = useState('');
  const [guidSeleccionado, setGuidSeleccionado] = useState(null);

  useEffect(() => {
    obtenerAutores();
  }, []);

  const obtenerAutores = () => {
    axios.get('/api/Autor')
      .then(response => {
        setAutores(response.data);
        setError('');
      })
      .catch(err => {
        setError('No se pudo cargar la lista de autores.');
        console.error(err);
      });
  };

  const validarFormulario = () => {
    if (!nombre.trim() || !apellido.trim() || !fechaNacimiento.trim()) {
      alert('Todos los campos son obligatorios.');
      return false;
    }
    const fecha = new Date(fechaNacimiento);
    const hoy = new Date();
    hoy.setHours(0, 0, 0, 0);
    if (fecha > hoy) {
      alert('La fecha de nacimiento no puede ser mayor a hoy.');
      return false;
    }
    return true;
  };

  const limpiarFormulario = () => {
    setNombre('');
    setApellido('');
    setFechaNacimiento('');
    setGuidSeleccionado(null);
  };

  const crearAutor = () => {
    if (!validarFormulario()) return;
    axios.post('/api/Autor', { nombre, apellido, fechaNacimiento })
      .then(() => {
        obtenerAutores();
        limpiarFormulario();
      })
      .catch(err => {
        alert('Error al crear autor.');
        console.error(err);
      });
  };

  const actualizarAutor = () => {
    if (!validarFormulario()) return;
    axios.put(`/api/Autor/${guidSeleccionado}`, {
      autorLibroGuid: guidSeleccionado,
      nombre,
      apellido,
      fechaNacimiento
    })
      .then(() => {
        obtenerAutores();
        limpiarFormulario();
      })
      .catch(err => {
        alert('Error al actualizar autor.');
        console.error(err);
      });
  };

  const eliminarAutor = (guid) => {
    if (!window.confirm('Â¿Seguro que deseas eliminar este autor?')) return;
    axios.delete(`/api/Autor/${guid}`)
      .then(() => obtenerAutores())
      .catch(err => {
        alert('Error al eliminar autor.');
        console.error(err);
      });
  };

  const seleccionarParaEditar = (autor) => {
    setGuidSeleccionado(autor.autorLibroGuid);
    setNombre(autor.nombre);
    setApellido(autor.apellido);
    setFechaNacimiento(autor.fechaNacimiento.split('T')[0]);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>GestiÃ³n de Autores</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <input type="text" placeholder="Nombre" value={nombre} onChange={e => setNombre(e.target.value)} />
      <input type="text" placeholder="Apellido" value={apellido} onChange={e => setApellido(e.target.value)} />
      <input type="date" placeholder="Fecha Nacimiento" value={fechaNacimiento} onChange={e => setFechaNacimiento(e.target.value)} />

      <button onClick={guidSeleccionado ? actualizarAutor : crearAutor}>
        {guidSeleccionado ? 'Actualizar' : 'Crear'}
      </button>
      <button onClick={limpiarFormulario}>Cancelar</button>

      <hr />

      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Fecha Nacimiento</th>
            <th>GUID</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {autores.map(autor => (
            <tr key={autor.autorLibroGuid}>
              <td>{autor.autorLibroId}</td>
              <td>{autor.nombre}</td>
              <td>{autor.apellido}</td>
              <td>{autor.fechaNacimiento.split('T')[0]}</td>
              <td>{autor.autorLibroGuid}</td>
              <td>
                <button onClick={() => seleccionarParaEditar(autor)}>Editar</button>
                <button onClick={() => eliminarAutor(autor.autorLibroGuid)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AutorList;
