import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import AutorList from './AutorList';

const App = () => {
  const [estaLogueado, setEstaLogueado] = useState(false);

  useEffect(() => {
    // Lee al iniciar si est치 logueado
    setEstaLogueado(localStorage.getItem('logueado') === 'true');

    // Escuchar evento storage para cambios en otras pesta침as (opcional)
    const onStorageChange = () => {
      setEstaLogueado(localStorage.getItem('logueado') === 'true');
    };
    window.addEventListener('storage', onStorageChange);

    return () => {
      window.removeEventListener('storage', onStorageChange);
    };
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={estaLogueado ? <Navigate to="/autores" /> : <Login setEstaLogueado={setEstaLogueado} />} />
        <Route path="/autores" element={estaLogueado ? <AutorList /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
