import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');
  const [mostrarRecuperacion, setMostrarRecuperacion] = useState(false);
  const [respuesta, setRespuesta] = useState('');
  const navigate = useNavigate();

  const manejarLogin = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ usuario: usuario.toLowerCase(), password }),
      });

      const data = await res.json();

      if (res.ok) {
        alert(data.mensaje);
        localStorage.setItem('logueado', 'true');
        navigate('/autores');
      } else {
        alert(data.mensaje || 'Credenciales incorrectas');
      }
    } catch (err) {
      console.error(err);
      alert('Error al iniciar sesión');
    }
  };

  const manejarRecuperacion = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/recuperar', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ usuario: usuario.toLowerCase(), respuesta }),
      });
      const data = await res.json();

      if (res.ok) {
        alert(data.mensaje);
      } else {
        alert(data.mensaje || 'Respuesta incorrecta');
      }
    } catch (err) {
      console.error(err);
      alert('Error al recuperar contraseña');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Iniciar Sesión</h2>
      <input
        style={styles.input}
        type="text"
        placeholder="Usuario"
        value={usuario}
        onChange={(e) => setUsuario(e.target.value)}
      />
      <input
        style={styles.input}
        type="password"
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button style={styles.button} onClick={manejarLogin}>
        Entrar
      </button>
      <p style={styles.link} onClick={() => setMostrarRecuperacion(true)}>
        ¿Olvidaste tu contraseña?
      </p>

      {mostrarRecuperacion && (
        <div style={styles.recoveryBox}>
          <h3>Recuperar contraseña</h3>
          <p>Pregunta secreta: ¿Nombre de tu mascota?</p>
          <input
            style={styles.input}
            type="text"
            placeholder="Respuesta"
            value={respuesta}
            onChange={(e) => setRespuesta(e.target.value)}
          />
          <button style={styles.buttonSecondary} onClick={manejarRecuperacion}>
            Recuperar
          </button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '400px',
    margin: '60px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  title: {
    textAlign: 'center',
    marginBottom: '20px',
    color: '#333',
  },
  input: {
    display: 'block',
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    fontSize: '16px',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#3498db',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  buttonSecondary: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#2ecc71',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  link: {
    textAlign: 'center',
    color: '#2980b9',
    cursor: 'pointer',
    marginTop: '10px',
  },
  recoveryBox: {
    marginTop: '20px',
    padding: '10px',
    backgroundColor: '#f3f3f3',
    borderRadius: '6px',
  },
};

export default Login;
