// Register.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');
  const [respuesta, setRespuesta] = useState('');
  const navigate = useNavigate();

  const manejarRegistro = async () => {
    if (!usuario || !password || !respuesta) {
      alert('Todos los campos son obligatorios');
      return;
    }

    try {
      const res = await fetch('https://mongodbbacken.onrender.com/api/usuarios', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          usuario: usuario.toLowerCase(),
          password,
          pregunta: 'Nombre de tu primera mascota',
          respuesta,
        }),
      });

      const data = await res.json();

      if (res.ok) {
        alert('Usuario registrado correctamente');
        navigate('/');
      } else {
        alert(data.mensaje || 'Error al registrar usuario');
      }
    } catch (err) {
      console.error(err);
      alert('Error en el servidor');
    }
  };

  return (
    <div style={styles.background}>
      <div style={styles.container}>
        <h2 style={styles.title}>Crear Cuenta</h2>
        <p style={styles.subtitle}>Completa el formulario para registrarte</p>

        <input
          style={styles.input}
          type="text"
          placeholder="Nombre de usuario"
          value={usuario}
          onChange={(e) => setUsuario(e.target.value)}
        />

        <input
          style={styles.input}
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <p style={styles.label}>
          Pregunta secreta: <strong>Nombre de tu primera mascota</strong>
        </p>

        <input
          style={styles.input}
          type="text"
          placeholder="Respuesta"
          value={respuesta}
          onChange={(e) => setRespuesta(e.target.value)}
        />

        <button style={styles.button} onClick={manejarRegistro}>
          Registrar
        </button>

        <p style={styles.link} onClick={() => navigate('/')}>
          ¿Ya tienes cuenta? <span style={styles.linkAccent}>Inicia sesión aquí</span>
        </p>
      </div>
    </div>
  );
};

const styles = {
  background: {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #f6d365 0%, #fda085 100%)',
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
  },
  container: {
    width: '100%',
    maxWidth: '420px',
    padding: '30px',
    borderRadius: '12px',
    backgroundColor: '#ffffff',
    boxShadow: '0 10px 25px rgba(0,0,0,0.15)',
    textAlign: 'center',
  },
  title: {
    fontSize: '28px',
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: '10px',
  },
  subtitle: {
    fontSize: '14px',
    color: '#7f8c8d',
    marginBottom: '20px',
  },
  label: {
    fontSize: '14px',
    textAlign: 'left',
    color: '#555',
    margin: '10px 0 5px 0',
  },
  input: {
    width: '100%',
    padding: '12px',
    margin: '8px 0',
    border: '1px solid #ddd',
    borderRadius: '8px',
    fontSize: '16px',
    transition: 'border-color 0.3s ease',
    outline: 'none',
  },
  button: {
    width: '100%',
    padding: '12px',
    marginTop: '15px',
    backgroundColor: '#27ae60',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: 'bold',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  link: {
    fontSize: '14px',
    color: '#34495e',
    marginTop: '20px',
    cursor: 'pointer',
  },
  linkAccent: {
    color: '#27ae60',
    fontWeight: 'bold',
  },
};

export default Register;
