import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');
  const [respuesta, setRespuesta] = useState('');
  const navigate = useNavigate();

  const manejarRegistro = async () => {
    if (!usuario || !password || !respuesta) {
      alert('Todos los campos son obligatorios');
      return;
    }

    try {
      const res = await fetch('https://mongodbbacken.onrender.com/api/usuarios', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          usuario: usuario.toLowerCase(),
          password,
          pregunta: 'Nombre de tu primera mascota',
          respuesta,
        }),
      });

      const data = await res.json();

      if (res.ok) {
        alert('Usuario registrado correctamente');
        navigate('/');
      } else {
        alert(data.mensaje || 'Error al registrar usuario');
      }
    } catch (err) {
      console.error(err);
      alert('Error en el servidor');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Registrar Usuario</h2>
      <input
        style={styles.input}
        type="text"
        placeholder="Nombre de usuario"
        value={usuario}
        onChange={(e) => setUsuario(e.target.value)}
      />
      <input
        style={styles.input}
        type="password"
        placeholder="ContraseÃ±a"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <p style={{ marginBottom: '5px' }}>
        Pregunta secreta: <b>Nombre de tu primera mascota</b>
      </p>
      <input
        style={styles.input}
        type="text"
        placeholder="Respuesta"
        value={respuesta}
        onChange={(e) => setRespuesta(e.target.value)}
      />
      <button style={styles.button} onClick={manejarRegistro}>
        Registrar
      </button>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '400px',
    margin: '60px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#fff',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  title: {
    textAlign: 'center',
    marginBottom: '20px',
    color: '#333',
  },
  input: {
    display: 'block',
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    fontSize: '16px',
  },
  button: {
    width: '100%',
    padding: '10px',
    backgroundColor: '#2ecc71',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default Register;
