import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import AutorList from './AutorList';

const App = () => {
  const [estaLogueado, setEstaLogueado] = useState(false);
  const [usuarioActual, setUsuarioActual] = useState(null);

  useEffect(() => {
    // Lee al iniciar si est치 logueado y el usuario
    const logueado = sessionStorage.getItem('logueado') === 'true';
    const usuario = sessionStorage.getItem('usuario');
    setEstaLogueado(logueado);
    setUsuarioActual(usuario);

    // Escuchar evento storage para cambios en otras pesta침as (opcional)
    const onStorageChange = () => {
      const logueado = sessionStorage.getItem('logueado') === 'true';
      const usuario = sessionStorage.getItem('usuario');
      setEstaLogueado(logueado);
      setUsuarioActual(usuario);
    };
    window.addEventListener('storage', onStorageChange);

    return () => {
      window.removeEventListener('storage', onStorageChange);
    };
  }, []);

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            estaLogueado
              ? <Navigate to="/autores" />
              : <Login setEstaLogueado={setEstaLogueado} setUsuarioActual={setUsuarioActual} />
          }
        />
        <Route
          path="/autores"
          element={
            estaLogueado
              ? <AutorList usuarioActual={usuarioActual} setEstaLogueado={setEstaLogueado} setUsuarioActual={setUsuarioActual} />
              : <Navigate to="/" />
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
